import React, { useState } from "react";
import axios from "axios";

// Define the API URL as a constant
const API_URL = "http://localhost:5000";

// Configure axios defaults
axios.defaults.withCredentials = false; // Disable sending credentials
axios.defaults.headers.common["Access-Control-Allow-Origin"] = "*"; // Allow all origins

// Function to format disease names for display
const formatDiseaseName = (name) => {
  return name
    .split("_")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
    .join(" ");
};

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
    setError(null);
    setResult(null);
  };

  const handleSubmit = async () => {
    if (!image) {
      setError("Please select an image first");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    setLoading(true);
    setError(null);

    try {
      console.log("Sending request to:", `${API_URL}/predict`);
      console.log("Image being sent:", image);

      const response = await axios.post(`${API_URL}/predict`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Accept: "application/json",
        },
        validateStatus: function (status) {
          return status < 500; // Resolve only if the status code is less than 500
        },
      });

      console.log("Response received:", response.data);

      if (response.data.success) {
        setResult(response.data);
      } else {
        setError(response.data.error || "Prediction failed");
      }
    } catch (error) {
      console.error("Detailed error:", error);
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error("Error response:", error.response.data);
        console.error("Error status:", error.response.status);
        console.error("Error headers:", error.response.headers);
        setError(
          `Server error: ${
            error.response.data.error || error.response.statusText
          }`
        );
      } else if (error.request) {
        // The request was made but no response was received
        console.error("No response received:", error.request);
        setError(
          "No response from server. Please check if the backend is running."
        );
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error("Error setting up request:", error.message);
        setError(`Error: ${error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h2>Skin Disease Detection</h2>
      <div className="upload-section">
        <input
          type="file"
          onChange={handleImageChange}
          accept="image/*"
          className="file-input"
        />
        <button
          onClick={handleSubmit}
          disabled={loading || !image}
          className={`submit-button ${loading ? "loading" : ""}`}
        >
          {loading ? "Processing..." : "Submit"}
        </button>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <p className="help-text">
            If you're seeing a connection error, please ensure the backend
            server is running on port 5000.
          </p>
        </div>
      )}

      {result && (
        <div className="result-section">
          <h3>Prediction Result:</h3>
          <p className="disease-name">
            Disease: {formatDiseaseName(result.disease)}
          </p>
          <p className="warning-text">
            Note: Please consult a healthcare professional for accurate
            diagnosis.
          </p>
        </div>
      )}

      <div className="disclaimer">
        <p>
          Disclaimer: This tool is for educational purposes only and should not
          be used as a substitute for professional medical advice. Always
          consult with a qualified healthcare provider for proper diagnosis and
          treatment.
        </p>
      </div>
    </div>
  );
}

export default App;
