import React, { useState } from "react";
import axios from "axios";

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
    setError(null);
    setResult(null);
  };

  const handleSubmit = async () => {
    if (!image) {
      setError("Please select an image first");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(
        "http://localhost:5000/predict",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.success) {
        setResult(response.data);
      } else {
        setError(response.data.error || "Prediction failed");
      }
    } catch (error) {
      console.error("Error:", error);
      setError(
        error.response?.data?.error ||
          "Failed to connect to the server. Please ensure the backend is running on port 5000."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h2>Skin Disease Detection</h2>
      <div className="upload-section">
        <input
          type="file"
          onChange={handleImageChange}
          accept="image/*"
          className="file-input"
        />
        <button
          onClick={handleSubmit}
          disabled={loading || !image}
          className={`submit-button ${loading ? "loading" : ""}`}
        >
          {loading ? "Processing..." : "Submit"}
        </button>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <p className="help-text">
            If you're seeing a connection error, please ensure the backend
            server is running on port 5000.
          </p>
        </div>
      )}

      {result && (
        <div className="result-section">
          <h3>Prediction Result:</h3>
          <p className="disease-name">Disease: {result.disease}</p>
          <p className="confidence">
            Confidence: {(result.confidence * 100).toFixed(2)}%
          </p>
          {result.confidence < 0.7 && (
            <p className="warning-text">
              Note: This prediction has low confidence. Please consult a
              healthcare professional for accurate diagnosis.
            </p>
          )}
        </div>
      )}

      <div className="disclaimer">
        <p>
          Disclaimer: This tool is for educational purposes only and should not
          be used as a substitute for professional medical advice. Always
          consult with a qualified healthcare provider for proper diagnosis and
          treatment.
        </p>
      </div>
    </div>
  );
}

export default App;
