import React, { useState } from "react";
import axios from "axios";

// Define the API URL as a constant
const API_URL = "http://localhost:5000";

// Remove the global axios defaults as they can cause CORS issues
// Configure axios for this specific instance instead

// Function to format disease names for display
const formatDiseaseName = (name) => {
  return name
    .split("_")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
    .join(" ");
};

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [previewUrl, setPreviewUrl] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      setError(null);
      setResult(null);
    }
  };

  const handleSubmit = async () => {
    if (!image) {
      setError("Please select an image first");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    setLoading(true);
    setError(null);

    try {
      console.log("Sending request to:", `${API_URL}/predict`);
      const response = await axios.post(`${API_URL}/predict`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Response received:", response.data);

      if (response.data.success) {
        setResult(response.data);
      } else {
        setError(response.data.error || "Prediction failed");
      }
    } catch (error) {
      console.error("Detailed error:", error);
      setError(
        error.response?.data?.error ||
          error.message ||
          "Failed to process the image. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  const renderColorAnalysis = (colorAnalysis) => {
    if (!colorAnalysis) return null;

    const { stats } = colorAnalysis;
    if (!stats) return null;

    return (
      <div className="color-analysis">
        <h4>Color Analysis:</h4>
        <div className="color-stats">
          <div className="stat-item">
            <span className="stat-label">Blue Detection:</span>
            <span
              className={`stat-value ${
                colorAnalysis.is_bluish ? "blue-detected" : ""
              }`}
            >
              {colorAnalysis.is_bluish
                ? "Blue components detected"
                : "No significant blue components"}
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Blue Ratio:</span>
            <span className="stat-value">
              {(stats.blue_ratio * 100).toFixed(1)}%
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">RGB Values:</span>
            <div className="rgb-values">
              <span className="rgb-value" style={{ color: "red" }}>
                R: {stats.mean_rgb[0].toFixed(1)}
              </span>
              <span className="rgb-value" style={{ color: "green" }}>
                G: {stats.mean_rgb[1].toFixed(1)}
              </span>
              <span className="rgb-value" style={{ color: "blue" }}>
                B: {stats.mean_rgb[2].toFixed(1)}
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      <h2>Skin Disease Detection</h2>
      <div className="upload-section">
        <input
          type="file"
          onChange={handleImageChange}
          accept="image/*"
          className="file-input"
        />
        {previewUrl && (
          <div className="image-preview">
            <img
              src={previewUrl}
              alt="Preview"
              style={{ maxWidth: "300px", marginTop: "10px" }}
            />
          </div>
        )}
        <button
          onClick={handleSubmit}
          disabled={loading || !image}
          className={`submit-button ${loading ? "loading" : ""}`}
        >
          {loading ? "Processing..." : "Submit"}
        </button>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <p className="help-text">
            If you're seeing a connection error, please ensure the backend
            server is running on port 5000.
          </p>
        </div>
      )}

      {result && (
        <div className="result-section">
          <h3>Prediction Result:</h3>
          {result.color_analysis && renderColorAnalysis(result.color_analysis)}
          <div className="predictions-list">
            {result.all_predictions.map((pred, index) => (
              <div
                key={index}
                className={`prediction-item ${
                  index === 0 ? "top-prediction" : ""
                }`}
              >
                <span className="disease-name">
                  {formatDiseaseName(pred.disease)}:
                </span>
                <span className="confidence">
                  {(pred.confidence * 100).toFixed(2)}%
                </span>
              </div>
            ))}
          </div>
          <p className="warning-text">
            Note: Please consult a healthcare professional for accurate
            diagnosis. These predictions are for educational purposes only.
          </p>
        </div>
      )}

      <div className="disclaimer">
        <p>
          Disclaimer: This tool is for educational purposes only and should not
          be used as a substitute for professional medical advice. Always
          consult with a qualified healthcare provider for proper diagnosis and
          treatment.
        </p>
      </div>
    </div>
  );
}

export default App;
